function pollMicrofono(){
 var total1s=0;
 var total0s=0;
 var procesar=false;
 
 auxTimeMicrofono= pulseInHighMicrofono();
 if (auxTimeMicrofono>-1){
  total1s=(auxTimeMicrofono/100);  
  for (i=1;i<=total1s;i++){
   //area.elt.placeholder += '1';
   stroke(255,255,255);
   rect(x,10,1,30);
   x++;
   if (globalBeginTrama==true){
	AddBitBufferRead(1,5);
   }
  }  
 }
 
 auxTimeMicrofono= pulseInLowMicrofono();
 if (auxTimeMicrofono>-1){
  total0s=(auxTimeMicrofono/100);
  if (total0s>=4){
   if (globalBeginTrama==false){
    globalBeginTrama=true;//Posible comienzo trama
    areaRX.elt.placeholder+='#';
	procesar=false;
   }
  }
  else{
   procesar=true;
  }  
  for (i=1;i<=total0s;i++){
   //area.elt.placeholder += '0';
   stroke(0,0,0);
   rect(x,10,1,30);
   x++;
   //if (total0s>=4){AddBitBufferRead(0,5);}//Si es comienzo de trama no metemos 0s
   if (globalBeginTrama==true && procesar==true){AddBitBufferRead(0,5);}//Si es comienzo de trama no metemos 0s 
  }
 }
}

