//800 Hz
function Pulse1buffer800Hz(){
 var muestreo=22050;
 var armonico=800; 
 //var longitud = (muestreo/armonico)*100;
 var longitud = (muestreo/10); //100 ms 22050*100/1000
 var ini=contSampleBufferSend;
 var fin=contSampleBufferSend+longitud;
 for (var t=ini;t<fin;++t) {    
  sampleBufferSend[t] = 2 * Math.sin(t);  // in the interval [-1,1]
  contSampleBufferSend++;
 } 
}

//Silencio
function Pulse0buffer800Hz(){
 var muestreo=22050;
 var armonico=800; 
 //var longitud = (muestreo/armonico)*100;
 var longitud = (muestreo/10); //100 ms
 var ini=contSampleBufferSend;
 var fin=contSampleBufferSend+longitud; 
 for (var t=ini;t<fin;++t) {  
  //samples[t] = 2 * Math.random() - 1;  // in the interval [-1,1]  
  sampleBufferSend[t] = 0;
  contSampleBufferSend++;
 }
}


function EnviaRTTY5Aire(cadena){
//Trama
//Pos 0: .
//Pos 1: tamanio
//Pos 2: CRC 
//Pos 3..n: TEXTO
//Pos n: .

 //var bufferSend=[];
 var cadByte='';
 var cadAux='';
 var codeASCII=0;
 var i=0;
 var j=0;
 var longitud=0;
 var codCRC=30;
 
 contSampleBufferSend=0; 
 
 longitud=cadena.length;
 cadena=":::B:"+String.fromCharCode(longitud,codCRC)+cadena+":"; //Meto inicio trama y fin trama
 for (i=0;i<(cadena.length-1);i++){  
  codeASCII=cadena.charCodeAt(i);
  switch(codeASCII){
   case 32:codeASCII=27;//Espacio blanco
    break;
   case 44:codeASCII=28;//Punto
    break;
   case 46:codeASCII=29;//Coma
	break;
   case 58:codeASCII=31//Dos puntos
    break;
   case 63:codeASCII=30;//Interrogacion
    break;
   default:	      
    if ((codeASCII>=65) && (codeASCII<=90)){codeASCII=codeASCII-65;}//letras mayusculas
    else{
     if ((codeASCII>=97) && (codeASCII<=122)){codeASCII=codeASCII-97;}//Letras minusculas
     else{codeASCII=27;}
    }    
  }
  cadByte=byteString(codeASCII);
  for (j=7;j>=3;j--){
   //console.log(cadByte.charAt(j));
   cadAux = cadByte.charAt(j);
   if (cadAux=='1'){
	Pulse1buffer800Hz();	
   }
   else{
	Pulse0buffer800Hz();	
   }
   //console.log(j+' '+cadAux);
   //console.log(j);
   //console.log(cadAux);
  }
  //console.log(cadByte);
 }
}


